#include <stdio.h>
#include <stdlib.h>
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>

// Initialize SDL and SDL_image
int init_sdl() {
    if (SDL_Init(SDL_INIT_VIDEO) != 0) {
        printf("SDL_Init failed: %s\n", SDL_GetError());
        return -1;
    }

    if (IMG_Init(IMG_INIT_PNG) != 0) {
        printf("IMG_Init failed: %s\n", IMG_GetError());
        return -1;
    }

    return 0;
}

int main(int argc, char* argv[]) {
    // Initialize SDL and SDL_image
    if (init_sdl() != 0) {
        return -1;
    }

    // Create a window and renderer
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, 640, 480, SDL_WINDOW_SHOWN);
    if (!window) {
        printf("SDL_CreateWindow failed: %s\n", SDL_GetError());
        return -1;
    }

    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (!renderer) {
        printf("SDL_CreateRenderer failed: %s\n", SDL_GetError());
        return -1;
    }

    // Load the background image
    SDL_Surface* background_surface = IMG_Load("background.png");
    if (!background_surface) {
        printf("IMG_Load failed: %s\n", IMG_GetError());
        return -1;
    }

    SDL_Texture* background_texture = SDL_CreateTextureFromSurface(renderer, background_surface);
    if (!background_texture) {
        printf("SDL_CreateTextureFromSurface failed: %s\n", SDL_GetError());
        return -1;
    }

    // Load the snake image
    SDL_Surface* snake_surface = IMG_Load("snake.png");
    if (!snake_surface) {
        printf("IMG_Load failed: %s\n", IMG_GetError());
        return -1;
    }

    SDL_Texture* snake_texture = SDL_CreateTextureFromSurface(renderer, snake_surface);
    if (!snake_texture) {
        printf("SDL_CreateTextureFromSurface failed: %s\n", SDL_GetError());
        return -1;
    }

    // Main game loop
    int running = 1;
    SDL_Event event;
    while (running) {
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                running = 0;
            }
        }

        // Clear the screen
        SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
        SDL_RenderClear(renderer);

        // Render the background
        SDL_RenderCopy(renderer, background_texture, NULL, NULL);

        // Render the snake
        SDL_Rect snake_rect = { 100, 100, 20, 20 };
        SDL_RenderCopy(renderer, snake_texture, NULL, &snake_rect);

        // Update the screen
        SDL_RenderPresent(renderer);

        // Cap the frame rate
        SDL_Delay(1000 / 60);
    }

    // Clean up
    SDL_DestroyTexture(background_texture);
    SDL_DestroyTexture(snake_texture);
    SDL_FreeSurface(background_surface);
    SDL_FreeSurface(snake_surface);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
