        switch (event.key.keysym.sym) {
            case SDLK_UP:
                snake.direction = 0;
                break;
            case SDLK_DOWN:
                snake.direction = 2;
                break;
            case SDLK_LEFT:
                snake.direction = 3;
                break;
            case SDLK_RIGHT:
                snake.direction = 1;
                break;
        }
    }
}

// Update game state
