    // Move snake
    switch (snake->direction) {
        case 0:
            snake->y -= BLOCK_SIZE;
            break;
        case 1:
            snake->x += BLOCK_SIZE;
            break;
        case 2:
            snake->y += BLOCK_SIZE;
            break;
        case 3:
            snake->x -= BLOCK_SIZE;
            break;
    }

    // Check collision with wall or self
    if (snake->x < 0 || snake->x >= SCREEN_WIDTH || snake->y < 0 || snake->y >= SCREEN_HEIGHT) {
        // Game over
    } else if (/* check collision with self */) {
        // Game over
    }
}

// Render game
void render_game(SDL_Renderer* renderer, Snake* snake) {
    // Clear screen
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
    SDL_RenderClear(renderer);

    // Draw snake
    for (int i = 0; i < snake->length; i++) {
        SDL_Rect rect;
        rect.x = snake->x + (i * BLOCK_SIZE);
        rect.y = snake->y;
        rect.w = BLOCK_SIZE;
        rect.h = BLOCK_SIZE;
        SDL_SetRenderDrawColor(renderer, 255, 255, 255, 255);
        SDL_RenderFillRect(renderer, &rect);
    }

    // Draw food
    SDL_Rect food_rect;
    food_rect.x = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
    food_rect.y = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;
    food_rect.w = BLOCK_SIZE;
    food_rect.h = BLOCK_SIZE;
    SDL_SetRenderDrawColor(renderer, 255, 0, 0, 255);
    SDL_RenderFillRect(renderer, &food_rect);

    // Update screen
    SDL_RenderPresent(renderer);
}

// Load image
void load_image(SDL_Renderer* renderer, char* filename, SDL_Texture** texture) {
    SDL_Surface* surface = IMG_Load(filename);
    *texture = SDL_CreateTextureFromSurface(renderer, surface);
    SDL_FreeSurface(surface);
}
```

### `main.c` (Entry Point)

```c
#include "snake.h"
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>

int main() {
    // Initialize SDL
    SDL_Init(SDL_INIT_VIDEO);
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Initialize game state
    Snake snake;
    init_game(window, renderer);

    // Main game loop
    while (1) {
        // Handle events
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            handle_events(event);
        }

        // Update game state
        update_game(&snake);

        // Render game
        render_game(renderer, &snake);

        // Cap framerate
        SDL_Delay(1000 / FPS);
    }

    // Clean up
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();
    return 0;
}
```

