                ephemeral: true,
            });
            return;
        }

        // Ban the member
        await member.ban({
            reason: 'Ban reason',
        });

        // Send a success message
        await interaction.reply({
            content: `Member ${member.user.tag} has been banned!`,
            ephemeral: false,
        });
    }
});

