        istringstream lineStream(fileContents.substr(i * (line.length() + 1), line.length() + 1));
        string currentLine;
        getline(lineStream, currentLine);
        istringstream wordStream(currentLine);
        string currentWord;
        while (wordStream >> currentWord) {
            if (currentWord == word) {
                wordCount++;
            }
        }
    }

    // Display the results
    cout << "The word '" << word << "' occurs " << wordCount << " times." << endl;

    return 0;
}
