    - Repeat the game loop until the user quits the game.

### Code Example

Here's an example of how to implement the snake game in C:
```c
#include <graphics.h>
#include <stdlib.h>

// Define the game's dimensions
#define BOARD_WIDTH  20
#define BOARD_HEIGHT 15

// Define the snake's initial position and direction
#define SNAKE_START_X  5
#define SNAKE_START_Y  5
#define SNAKE_DIRECTION 1

// Define the snake's initial length
#define SNAKE_LENGTH  3

// Define the food's initial position
#define FOOD_X  10
#define FOOD_Y  10

// Define the game's score
int score = 0;

// Define the snake's position and direction
int snake_x[SNAKE_LENGTH];
int snake_y[SNAKE_LENGTH];
int snake_direction = SNAKE_DIRECTION;

// Define the food's position
int food_x = FOOD_X;
int food_y = FOOD_Y;

// Initialize the game
void init_game() {
    // Initialize the graphics mode
    initgraph(BOARD_WIDTH, BOARD_HEIGHT);

    // Set the background color
    setbkcolor(BLACK);

    // Initialize the snake's position and direction
    snake_x[0] = SNAKE_START_X;
    snake_y[0] = SNAKE_START_Y;
    for (int i = 1; i < SNAKE_LENGTH; i++) {
        snake_x[i] = snake_x[i - 1];
        snake_y[i] = snake_y[i - 1];
    }

    // Set the food's position
    food_x = rand() % BOARD_WIDTH;
    food_y = rand() % BOARD_HEIGHT;
}

// Draw the game board
void draw_board() {
    // Draw the game board using the rectangle() function
    rectangle(0, 0, BOARD_WIDTH - 1, BOARD_HEIGHT - 1);
}

// Draw the snake
void draw_snake() {
    // Set the snake's color
    setcolor(GREEN);

    // Draw the snake's body using the line() function
    for (int i = 0; i < SNAKE_LENGTH - 1; i++) {
        line(snake_x[i], snake_y[i], snake_x[i + 1], snake_y[i + 1]);
    }

    // Draw the snake's head using the circle() function
    circle(snake_x[SNAKE_LENGTH - 1], snake_y[SNAKE_LENGTH - 1], 5);
}

// Draw the food
void draw_food() {
    // Set the food's color
    setcolor(RED);

    // Draw the food using the circle() function
    circle(food_x, food_y, 5);
}

// Handle user input
void handle_input() {
    // Get user input using the getch() function
    int input = getch();

    // Update the snake's direction based on the user's input
    if (input == 'w' && snake_direction != 2) {
        snake_direction = 1;
    } else if (input == 's' && snake_direction != 1) {
        snake_direction = 2;
    } else if (input == 'a' && snake_direction != 4) {
        snake_direction = 3;
    } else if (input == 'd' && snake_direction != 3) {
        snake_direction = 4;
    }
}

// Move the snake
void move_snake() {
    // Set the snake's color
    setcolor(GREEN);

    // Draw the snake's body using the line() function
    for (int i = SNAKE_LENGTH - 1; i > 0; i--) {
        snake_x[i] = snake_x[i - 1];
        snake_y[i] = snake_y[i - 1];
    }

    // Update the snake's position and direction
    if (snake_direction == 1) {
        snake_y[0]++;
    } else if (snake_direction == 2) {
        snake_y[0]--;
    } else if (snake_direction == 3) {
        snake_x[0]--;
    } else if (snake_direction == 4) {
        snake_x[0]++;
    }
}

// Check for collisions
void check_collisions() {
    // Check for collisions with the game board's boundaries
    if (snake_x[0] < 0 || snake_x[0] >= BOARD_WIDTH || snake_y[0] < 0 || snake_y[0] >= BOARD_HEIGHT) {
        // Game over
        exit(0);
    }

    // Check for collisions with the snake's body
    for (int i = 1; i < SNAKE_LENGTH; i++) {
        if (snake_x[0] == snake_x[i] && snake_y[0] == snake_y[i]) {
            // Game over
            exit(0);
        }
    }

    // Check for collisions with the food
    if (snake_x[0] == food_x && snake_y[0] == food_y) {
        // Increase the snake's length
        SNAKE_LENGTH++;

        // Set the food's position
        food_x = rand() % BOARD_WIDTH;
        food_y = rand() % BOARD_HEIGHT;

        // Increase the game's score
        score++;
    }
}

// Update the game
void update_game() {
    // Update the snake's position and length
    move_snake();

    // Update the food's position
    food_x = rand() % BOARD_WIDTH;
    food_y = rand() % BOARD_HEIGHT;

    // Update the game's score
    settextstyle(0, 0, 2);
    outtextxy(BOARD_WIDTH / 2 - 20, BOARD_HEIGHT / 2, "Score: ");
    settextstyle(0, 0, 1);
    outtextxy(BOARD_WIDTH / 2 + 10, BOARD_HEIGHT / 2, itoa(score, 0, 10));
}

// Display the game
void display_game() {
    // Set the text color
    setcolor(WHITE);

    // Display the game's score using the outtext() function
    settextstyle(0, 0, 2);
    outtextxy(BOARD_WIDTH / 2 - 20, BOARD_HEIGHT / 2, "Score: ");
    settextstyle(0, 0, 1);
    outtextxy(BOARD_WIDTH / 2 + 10, BOARD_HEIGHT / 2, itoa(score, 0, 10));
}

// Main function
int main() {
    // Initialize the game
    init_game();

    // Draw the game board
    draw_board();

    // Draw the snake
    draw_snake();

    // Draw the food
    draw_food();

    // Handle user input
    handle_input();

    // Repeat the game loop until the user quits the game
    while (true) {
        // Move the snake
        move_snake();

        // Check for collisions
        check_collisions();

        // Update the game
        update_game();

        // Display the game
        display_game();

        // Delay for 100 milliseconds
        delay(100);
    }

    // Close the graphics mode
    closegraph();

    return 0;
}
```

