  for (let i = 0; i < numbers.length; i++) {
    const number = numbers[i];

    // Check if the number is prime
    if (isPrime(number)) {
      console.log(`Number ${number} is prime`);
    }
  }
}

// Function to check if a number is prime
function isPrime(number) {
  if (number <= 1) {
    return false;
  }

  for (let i = 2; i < number; i++) {
    if (number % i === 0) {
