public class PowerOfThree {
    public static boolean isPowerOfThree(int n) {
        // Check if n is positive
        if (n <= 0) {
            return false;
        }

        // Calculate the logarithm of n with base 3
        double log = Math.log(n) / Math.log(3);

        // Check if log is an integer
        return log == (int) log;
    }
}
