        handleEvents();

        // Update game
        update();

        // Render game
        render();

        // Clean up
        clean();
    }

    // Clean up
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}

