    // Initialize snake
    snake.x = WINDOW_WIDTH / 2;
    snake.y = WINDOW_HEIGHT / 2;
    snake.length = 1;
    snake.direction = 0;

    // Initialize food
    food.x = rand() % (WINDOW_WIDTH - 50) + 25;
    food.y = rand() % (WINDOW_HEIGHT - 50) + 25;
