        exit(0);
    }

    // Check for food collision
    if (x == food.x && y == food.y) {
        // Eat food
        snake.length++;
        // Generate new food
        food.x = rand() % (WINDOW_WIDTH - 50) + 25;
        food.y = rand() % (WINDOW_HEIGHT - 50) + 25;
    }

    // Update snake position
    snake.x = x;
    snake.y = y;
}

