const { Client, GatewayIntentBits, Partials } = require("discord.js");
const { REST } = require("@discordjs/rest");
const { Routes } = require("discord-api-types/v9");

// Create a new Discord client
const client = new Client({
  intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMessages],
  partials: [Partials.Message],
});

// Define the ban command
const banCommand = {
  name: "ban",
  description: "Bans a user from the server",
  options: [
    {
      name: "user",
      description: "The user to ban",
      type: "USER",
      required: true,
    },
  ],
  execute: async (interaction) => {
    // Get the user ID from the command options
    const userId = interaction.options.getUser("user").id;

    // Ban the user
    await interaction.guild.members.ban(userId);

    // Respond with a success message
    await interaction.reply({
      content: `User ${userId} has been banned from the server.`,
      ephemeral: true,
    });
  },
};

// Register the ban command
client.commands = new Map();
client.commands.set(banCommand.name, banCommand);

// Create a REST client
const rest = new REST({ version: "9" }).setToken(process.env.TOKEN);

// Register the commands
(async () => {
  try {
    await rest.put(Routes.applicationCommands(process.env.CLIENT_ID), {
      body: Array.from(client.commands.values()),
    });

    console.log("Successfully registered commands.");
  } catch (error) {
    console.error(error);
  }
})();

// Login to Discord with your bot token
client.login(process.env.TOKEN);
