void init_game(struct Game *game) {
    // Initialize the game state
    game->state = 1;

    // Initialize the snake
    game->snake.x = WINDOW_WIDTH / 2;
    game->snake.y = WINDOW_HEIGHT / 2;
    game->snake.direction = 0;
    game->snake.length = 1;

    // Initialize the food
    game->food.x = rand() % (WINDOW_WIDTH - FOOD_SIZE) + FOOD_SIZE / 2;
    game->food.y = rand() % (WINDOW_HEIGHT - FOOD_SIZE) + FOOD_SIZE / 2;
    game->food.type = 0;

    // Initialize the score and high score
    game->score = 0;
    game->high_score = 0;
}

void update_game(struct Game *game) {
    // Update the snake's position
    switch (game->snake.direction) {
        case 0:
            game->snake.x += SNAKE_SPEED;
            break;
        case 1:
            game->snake.y += SNAKE_SPEED;
            break;
        case 2:
            game->snake.x -= SNAKE_SPEED;
            break;
        case 3:
            game->snake.y -= SNAKE_SPEED;
            break;
    }

    // Check for collisions with the edges of the screen
    if (game->snake.x < 0 || game->snake.x > WINDOW_WIDTH - SNAKE_SIZE || game->snake.y < 0 || game->snake.y > WINDOW_HEIGHT - SNAKE_SIZE) {
        game->state = 0;
    }

    // Check for collisions with the food
    if (game->snake.x == game->food.x && game->snake.y == game->food.y) {
        // Increase the snake's length
        game->snake.length++;

        // Generate new food
        game->food.x = rand() % (WINDOW_WIDTH - FOOD_SIZE) + FOOD_SIZE / 2;
        game->food.y = rand() % (WINDOW_HEIGHT - FOOD_SIZE) + FOOD_SIZE / 2;
        game->food.type = 0;

        // Increase the score
        game->score++;
    }

    // Check for collisions with the snake's tail
    for (int i = 1; i < game->snake.length; i++) {
        if (game->snake.x == game->snake.tail[i].x && game->snake.y == game->snake.tail[i].y) {
            game->state = 0;
        }
    }
}

void handle_input(struct Game *game) {
    // Handle user input
    SDL_Event event;
    while (SDL_PollEvent(&event)) {
        switch (event.type) {
            case SDL_QUIT:
                game->state = 0;
                break;
            case SDL_KEYDOWN:
                switch (event.key.keysym.sym) {
                    case SDLK_UP:
                        if (game->snake.direction != 2) {
                            game->snake.direction = 0;
                        }
                        break;
                    case SDLK_DOWN:
                        if (game->snake.direction != 0) {
                            game->snake.direction = 1;
                        }
                        break;
                    case SDLK_LEFT:
                        if (game->snake.direction != 1) {
                            game->snake.direction = 2;
                        }
                        break;
                    case SDLK_RIGHT:
                        if (game->snake.direction != 2) {
                            game->snake.direction = 3;
                        }
                        break;
                }
                break;
        }
    }
}

void render_game(struct Game *game) {
