SDL_Color white = {255, 255, 255, 255};
SDL_Color green = {0, 255, 0, 255};
SDL_Color red = {255, 0, 0, 255};

// Initialize SDL
int main(int argc, char* argv[]) {
    SDL_Init(SDL_INIT_EVERYTHING);
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, 640, 480, 0);
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

    // Load images
    SDL_Surface* snakeSurface = SDL_LoadBMP("snake.bmp");
    SDL_Surface* foodSurface = SDL_LoadBMP("food.bmp");
    SDL_Surface* scoreSurface = SDL_LoadBMP("score.bmp");

    // Create textures
    SDL_Texture* snakeTexture = SDL_CreateTextureFromSurface(renderer, snakeSurface);
    SDL_Texture* foodTexture = SDL_CreateTextureFromSurface(renderer, foodSurface);
    SDL_Texture* scoreTexture = SDL_CreateTextureFromSurface(renderer, scoreSurface);

    // Initialize snake position
    int snakeLength = 3;
    int snakeX[snakeLength];
    int snakeY[snakeLength];
    for (int i = 0; i < snakeLength; i++) {
        snakeX[i] = 10 + i;
        snakeY[i] = 10;
    }

    // Initialize direction
    int dx = 0;
    int dy = -1;

    // Generate random food coordinates
    int foodX = rand() % 32;
    int foodY = rand() % 24;

    // Game loop
    while (true) {
        // Handle user input
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            switch (event.type) {
                case SDL_QUIT:
                    return 0;
                case SDL_KEYDOWN:
                    switch (event.key.keysym.sym) {
                        case SDLK_UP:
                            // Handle up arrow key press
                            if (dy != 1) {
                                dx = 0;
                                dy = -1;
                            }
                            break;
                        case SDLK_DOWN:
                            // Handle down arrow key press
                            if (dy != -1) {
                                dx = 0;
                                dy = 1;
                            }
                            break;
                        case SDLK_LEFT:
                            // Handle left arrow key press
                            if (dx != 1) {
                                dx = -1;
                                dy = 0;
                            }
                            break;
                        case SDLK_RIGHT:
                            // Handle right arrow key press
                            if (dx != -1) {
                                dx = 1;
                                dy = 0;
                            }
                            break;
                    }
            }
        }

        // Update snake position
        for (int i = snakeLength - 1; i > 0; i--) {
            snakeX[i] = snakeX[i - 1];
            snakeY[i] = snakeY[i - 1];
        }
        snakeX[0] = snakeX[0] + dx;
        snakeY[0] = snakeY[0] + dy;

        // Check for collisions
        for (int i = 1; i < snakeLength; i++) {
            if (snakeX[0] == snakeX[i] && snakeY[0] == snakeY[i]) {
                // Game Over
                break;
            }
        }

        // Check for food collision
        if (snakeX[0] == foodX && snakeY[0] == foodY) {
            // Add a new segment to the snake
            snakeLength++;
            snakeX[snakeLength - 1] = snakeX[snakeLength - 2];
            snakeY[snakeLength - 1] = snakeY[snakeLength - 2];

            // Generate new food coordinates
            foodX = rand() % 32;
            foodY = rand() % 24;
        }

        // Handle game over
        if (snakeX[0] < 0 || snakeX[0] >= 32 || snakeY[0] < 0 || snakeY[0] >= 24) {
            // Game Over
            break;
        }

