// Function to check if a number is prime
function isPrime(num) {
  // If the number is less than or equal to 1, it's not prime
  if (num <= 1) return false;

  // Check if the number is divisible by any number between 2 and its square root
  for (let i = 2; i <= Math.sqrt(num); i++) {
    if (num % i === 0) {
      return false; // Not prime if divisible
    }
  }

  return true; // Prime if no divisors found
}

// Main function to check if any array element is prime
function checkPrimeNumbers(arr) {
  // Iterate through the array
  for (let num of arr) {
    // Check if the current number is prime
    if (isPrime(num)) {
      console.log(`The number ${num} is prime.`);
      return; // Stop checking further if a prime is found
    }
  }
}

