    std::string html;
    std::regex markdown_regex("\\|([^|]+)\\|");
    std::smatch match;

    while (std::regex_search(markdown, match, markdown_regex)) {
        std::string link = match[1];
        html += "<a href=\"" + link + "\">" + link + "</a><br>";
    }

    html += "<html><body>";
    html += markdown;
    html += "</body></html>";

    return html;
}

