  // Initialize the game
  srand(time(NULL));
  init_board(board);
  generate_food(board);
  for (int i = 0; i < SNAKE_LENGTH; i++) {
    snake[i][0] = BOARD_SIZE / 2;
    snake[i][1] = BOARD_SIZE / 2;
  }

  // Main game loop
  while (1) {
    // Handle user input
    handle_input(&direction);

    // Move the snake
