    // Check for collisions
    check_collisions(snake, board);

    // Generate new food if necessary
    if (score % FOOD_VALUE == 0) {
      generate_food(board);
    }

    // Update the game state
    animate_game(snake, board);
  }

  return 0;
}

// Initialize the game board
void init_board(int board[BOARD_SIZE][BOARD_SIZE]) {
  for (int i = 0; i < BOARD_SIZE; i++) {
    for (int j = 0; j < BOARD_SIZE; j++) {
      board[i][j] = 0;
    }
  }
}

// Draw the game board
void draw_board(int board[BOARD_SIZE][BOARD_SIZE]) {
  for (int i = 0; i < BOARD_SIZE; i++) {
    for (int j = 0; j < BOARD_SIZE; j++) {
      if (board[i][j] == 1) {
        printf("X");
      } else if (board[i][j] == 2) {
        printf("O");
      } else {
        printf(" ");
      }
    }
    printf("\n");
  }
}

// Move the snake
void move_snake(int snake[BOARD_SIZE][BOARD_SIZE], int direction) {
  for (int i = SNAKE_LENGTH - 1; i > 0; i--) {
    snake[i][0] = snake[i - 1][0];
    snake[i][1] = snake[i - 1][1];
  }

  switch (direction) {
    case 0:
      snake[0][1]--;
      break;
    case 1:
      snake[0][0]++;
      break;
    case 2:
      snake[0][1]++;
      break;
    case 3:
      snake[0][0]--;
      break;
  }

  // Wrap the snake around the edges of the board
  if (snake[0][0] < 0) {
    snake[0][0] = BOARD_SIZE - 1;
  } else if (snake[0][0] >= BOARD_SIZE) {
    snake[0][0] = 0;
  }

  if (snake[0][1] < 0) {
    snake[0][1] = BOARD_SIZE - 1;
  } else if (snake[0][1] >= BOARD_SIZE) {
    snake[0][1] = 0;
  }
