// Check for collisions
void check_collisions(int snake[BOARD_SIZE][BOARD_SIZE], int board[BOARD_SIZE][BOARD_SIZE]) {
  for (int i = 1; i < SNAKE_LENGTH; i++) {
    if (snake[0][0] == snake[i][0] && snake[0][1] == snake[i][1]) {
      // Game over
      printf("Game over!\n");
      exit(0);
