    // Game over
    printf("Game over!\n");
    exit(0);
  }

  if (board[snake[0][0]][snake[0][1]] == 1) {
    // Game over
    printf("Game over!\n");
    exit(0);
  }
}

// Generate new food
void generate_food(int board[BOARD_SIZE][BOARD_SIZE]) {
  int x, y;
  do {
    x = rand() % BOARD_SIZE;
    y = rand() % BOARD_SIZE;
  } while (board[x][y] != 0);

  board[x][y] = 2;
  food_x = x;
  food_y = y;
