// Function to check if a number is prime
function isPrime(num) {
  // Edge case: 0 and 1 are not prime
  if (num <= 1) return false;

  // Check if the number is divisible by any number between 2 and its square root
  for (let i = 2; i <= Math.sqrt(num); i++) {
    if (num % i === 0) return false;
  }

  // If the loop completes without finding a divisor, the number is prime
  return true;
}

// Function to check if any number in the array is prime
function checkPrime(arr) {
  // Iterate through the array
