```typescript
interface Quackable {
  quack(): void;
}

function quack(duck: Quackable) {
  duck.quack();
}

class Duck implements Quackable {
  quack() {
    console.log("Quack!");
  }
}

class RubberDuck implements Quackable {
  quack() {
    console.log("Squeak!");
  }
}

const duck = new Duck();
const rubberDuck = new RubberDuck();

quack(duck); // Output: "Quack!"
quack(rubberDuck); // Output: "Squeak!"
```

