```c
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>

#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600
#define BLOCK_SIZE 20

// Function prototypes
void init();
void loadMedia();
void close();
void update();
void render();
void handleEvents();

// Global variables
SDL_Window* gWindow = NULL;
SDL_Renderer* gRenderer = NULL;
SDL_Surface* gBackgroundSurface = NULL;
SDL_Texture* gBackgroundTexture = NULL;
SDL_Surface* gSnakeSurface = NULL;
SDL_Texture* gSnakeTexture = NULL;
SDL_Surface* gFoodSurface = NULL;
SDL_Texture* gFoodTexture = NULL;
int gSnakeX[100];
int gSnakeY[100];
int gSnakeLength;
int gFoodX;
int gFoodY;
int gDirection;

int main(int argc, char* argv[]) {
  // Initialize SDL
  if (SDL_Init(SDL_INIT_VIDEO) < 0) {
    printf("SDL could not initialize! SDL_Error: %s\n", SDL_GetError());
    return 1;
  }

  // Create window
  gWindow = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
  if (gWindow == NULL) {
    printf("Window could not be created! SDL_Error: %s\n", SDL_GetError());
    return 1;
  }

  // Create renderer
  gRenderer = SDL_CreateRenderer(gWindow, -1, SDL_RENDERER_ACCELERATED);
  if (gRenderer == NULL) {
    printf("Renderer could not be created! SDL_Error: %s\n", SDL_GetError());
    return 1;
  }

  // Load media
  loadMedia();

  // Initialize game variables
  init();

  // Main game loop
  bool quit = false;
  while (!quit) {
    // Handle events
    handleEvents();

    // Update game state
    update();

    // Render game
    render();

    // Update screen
    SDL_RenderPresent(gRenderer);
  }

  // Clean up
  close();

  return 0;
}

void init() {
  // Initialize game variables
  gSnakeX[0] = SCREEN_WIDTH / 2;
  gSnakeY[0] = SCREEN_HEIGHT / 2;
  gSnakeLength = 1;
  gFoodX = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
  gFoodY = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;
  gDirection = 0;
}

void loadMedia() {
  // Load background image
  gBackgroundSurface = IMG_Load("background.png");
  if (gBackgroundSurface == NULL) {
    printf("Unable to load image! SDL_Error: %s\n", SDL_GetError());
    return;
  }
  gBackgroundTexture = SDL_CreateTextureFromSurface(gRenderer, gBackgroundSurface);
  if (gBackgroundTexture == NULL) {
    printf("Unable to create texture! SDL_Error: %s\n", SDL_GetError());
    return;
  }

  // Load snake image
  gSnakeSurface = IMG_Load("snake.png");
  if (gSnakeSurface == NULL) {
    printf("Unable to load image! SDL_Error: %s\n", SDL_GetError());
    return;
  }
  gSnakeTexture = SDL_CreateTextureFromSurface(gRenderer, gSnakeSurface);
  if (gSnakeTexture == NULL) {
    printf("Unable to create texture! SDL_Error: %s\n", SDL_GetError());
    return;
  }

  // Load food image
  gFoodSurface = IMG_Load("food.png");
  if (gFoodSurface == NULL) {
    printf("Unable to load image! SDL_Error: %s\n", SDL_GetError());
    return;
  }
  gFoodTexture = SDL_CreateTextureFromSurface(gRenderer, gFoodSurface);
  if (gFoodTexture == NULL) {
    printf("Unable to create texture! SDL_Error: %s\n", SDL_GetError());
    return;
  }
}

void close() {
  // Free loaded images
  SDL_DestroyTexture(gBackgroundTexture);
  SDL_FreeSurface(gBackgroundSurface);
  gBackgroundSurface = NULL;
  SDL_DestroyTexture(gSnakeTexture);
  SDL_FreeSurface(gSnakeSurface);
  gSnakeSurface = NULL;
  SDL_DestroyTexture(gFoodTexture);
  SDL_FreeSurface(gFoodSurface);
  gFoodSurface = NULL;

  // Destroy window
  SDL_DestroyRenderer(gRenderer);
  SDL_DestroyWindow(gWindow);
  gWindow = NULL;
  gRenderer = NULL;

  // Quit SDL subsystems
  IMG_Quit();
  SDL_Quit();
}

void update() {
  // Update snake position
  for (int i = gSnakeLength; i > 0; i--) {
    gSnakeX[i] = gSnakeX[i - 1];
    gSnakeY[i] = gSnakeY[i - 1];
  }

  // Move snake
  switch (gDirection) {
    case 0:
      gSnakeX[0] += BLOCK_SIZE;
      break;
    case 1:
      gSnakeY[0] += BLOCK_SIZE;
      break;
    case 2:
      gSnakeX[0] -= BLOCK_SIZE;
      break;
    case 3:
      gSnakeY[0] -= BLOCK_SIZE;
      break;
  }

  // Check for collisions
  for (int i = 1; i < gSnakeLength; i++) {
    if (gSnakeX[0] == gSnakeX[i] && gSnakeY[0] == gSnakeY[i]) {
      // Game over
      return;
    }
  }

  // Check for food
  if (gSnakeX[0] == gFoodX && gSnakeY[0] == gFoodY) {
    // Eat food
    gSnakeLength++;
    gFoodX = rand() % (SCREEN_WIDTH / BLOCK_SIZE) * BLOCK_SIZE;
    gFoodY = rand() % (SCREEN_HEIGHT / BLOCK_SIZE) * BLOCK_SIZE;
  }
}

void render() {
  // Clear screen
  SDL_RenderClear(gRenderer);

  // Render background
  SDL_RenderCopy(gRenderer, gBackgroundTexture, NULL, NULL);

  // Render snake
  for (int i = 0; i < gSnakeLength; i++) {
    SDL_Rect snakeRect = {gSnakeX[i], gSnakeY[i], BLOCK_SIZE, BLOCK_SIZE};
    SDL_RenderCopy(gRenderer, gSnakeTexture, NULL, &snakeRect);
  }

  // Render food
  SDL_Rect foodRect = {gFoodX, gFoodY, BLOCK_SIZE, BLOCK_SIZE};
  SDL_RenderCopy(gRenderer, gFoodTexture, NULL, &foodRect);

  // Update screen
  SDL_RenderPresent(gRenderer);
}

void handleEvents() {
  SDL_Event e;
  while (SDL_PollEvent(&e) != 0) {
    if (e.type == SDL_QUIT) {
      // Quit game
      return;
    } else if (e.type == SDL_KEYDOWN) {
      // Handle key presses
      switch (e.key.keysym.sym) {
        case SDLK_UP:
          if (gDirection != 2) {
            gDirection = 1;
          }
          break;
        case SDLK_DOWN:
          if (gDirection != 1) {
            gDirection = 3;
          }
          break;
        case SDLK_LEFT:
          if (gDirection != 0) {
            gDirection = 2;
          }
          break;
        case SDLK_RIGHT:
          if (gDirection != 2) {
            gDirection = 0;
          }
          break;
      }
    }
  }
}
