const { Client, GatewayIntentBits, SlashCommandBuilder, SlashCommandStringOption } = require('discord.js');

// Create a new Discord client
const client = new Client({ intents: [GatewayIntentBits.Guilds] });

// Define the ban command
const banCommand = new SlashCommandBuilder()
  .setName('ban')
  .setDescription('Bans a user from the server.')
  .addUserOption(option => option.setName('user').setDescription('The user to ban').setRequired(true))
  .addStringOption(option => option.setName('reason').setDescription('The reason for the ban').setRequired(true));

