// Import the Discord.js library
const { Client, GatewayIntentBits, Partials, Collection } = require("discord.js");

// Create a new Discord client
const client = new Client({
  intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMessages],
  partials: [Partials.Message],
});

// Define a collection of commands
const commands = new Collection();

// Define the ban command
const banCommand = {
  name: "ban",
  description: "Bans a user from the server.",
  execute: async (interaction) => {
    // Get the user to ban
    const user = interaction.options.getUser("user");

    // Get the reason for the ban
    const reason = interaction.options.getString("reason") || "No reason provided";

    // Ban the user
    await interaction.guild.members.ban(user, { reason });

    // Send a confirmation message
    await interaction.reply({
      content: `**${user.tag}** has been banned from the server. Reason: ${reason}`,
      ephemeral: true,
    });
  },
};

// Add the ban command to the collection
commands.set(banCommand.name, banCommand);

// Register the commands
client.commands = commands;

// Login to Discord with the bot token
client.login("YOUR_BOT_TOKEN");
```

