function checkPrimeNumbers(arr) {
  // Loop through each number in the array
  for (let num of arr) {
    // Check if the number is prime
    if (isPrime(num)) {
      console.log(`The number ${num} is a prime number.`);
      return; // Stop checking further numbers
    }
