#include <iostream>  // For input/output operations
#include <fstream>   // For file input/output
#include <string>    // For string manipulation

using namespace std;  // To avoid typing "std::" before every standard library function

// Function to count the occurrences of a word in a text file
int countWordOccurrences(string word, string fileName) {
    int count = 0;  // Initialize the count to 0

    // Open the file for reading
    ifstream file(fileName);

    // Check if the file was successfully opened
    if (file.is_open()) {
        // Read the file line by line
        string line;
        while (getline(file, line)) {
            // Check if the word is present in the current line
            size_t pos = line.find(word);
            while (pos != string::npos) {
                count++;  // Increment the count if the word is found
                pos = line.find(word, pos + word.length());
            }
        }
        file.close();  // Close the file after reading
    } else {
        cout << "Error opening file: " << fileName << endl;
    }

    return count;  // Return the count of word occurrences
}

// Main function
int main() {
    // Define the word to search for
    string word = "example";

    // Define the file name
    string fileName = "text_file.txt";

    // Call the countWordOccurrences function
    int count = countWordOccurrences(word, fileName);

    // Print the count of word occurrences
    cout << "The word '" << word << "' occurs " << count << " times in the file." << endl;

    return 0;
}
```

