IMAGE load_image(char *filename) {
  IMAGE img;
  FILE *fp = fopen(filename, "rb");
  if (fp == NULL) {
    printf("Error loading image file: %s\n", filename);
    exit(1);
  }
  // Read the image header
  unsigned char magic[2];
  fread(magic, 1, 2, fp);
