    printf("Invalid image file format: %s\n", filename);
    exit(1);
  }
  // Read the image dimensions
  unsigned int size, width, height;
  fread(&size, 4, 1, fp);
  fread(&width, 4, 1, fp);
  fread(&height, 4, 1, fp);
  // Allocate memory for the image data
  img.data = malloc(width * height * 3);
  if (img.data == NULL) {
    printf("Error allocating memory for image data\n");
    exit(1);
  }
  // Read the image data
  fread(img.data, 1, width * height * 3, fp);
  // Calculate the image size
  img.width = width;
  img.height = height;
  fclose(fp);
  return img;
}

// Draw an image at a given position
void draw_image(IMAGE img, int x, int y) {
  for (int i = 0; i < img.height; i++) {
    for (int j = 0; j < img.width; j++) {
      int index = (i * img.width + j) * 3;
