### Code

```c
#include <SDL2/SDL.h>
#include <SDL2/SDL_image.h>
#include <stdio.h>
#include <stdlib.h>

// Define constants
#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600
#define FPS 60

// Define game structures
typedef struct {
    int x, y;
    int width, height;
} Rectangle;

typedef struct {
    int x, y;
    int width, height;
    int score;
} Level;

// Function to initialize SDL
void initSDL() {
