        printf("SDL initialization failed: %s\n", SDL_GetError());
        exit(1);
    }
}

// Function to load image
SDL_Texture* load_image(const char* filename) {
    SDL_Surface* surface = IMG_Load(filename);
    if (!surface) {
        printf("Failed to load image: %s\n", IMG_GetError());
        exit(1);
    }
    SDL_Texture* texture = SDL_CreateTextureFromSurface(SDL_GetRenderer(SDL_GetWindowFromID(SDL_GetWindowID(NULL))), surface);
    SDL_FreeSurface(surface);
    return texture;
}

// Function to render game
void render_game(SDL_Renderer* renderer, SDL_Texture* snake_texture, SDL_Texture* food_texture, Level* level) {
    SDL_Rect snake_rect = { level->snake_x, level->snake_y, level->snake_width, level->snake_height };
    SDL_Rect food_rect = { level->food_x, level->food_y, level->food_width, level->food_height };

    SDL_RenderClear(renderer);
    SDL_RenderCopy(renderer, snake_texture, NULL, &snake_rect);
    SDL_RenderCopy(renderer, food_texture, NULL, &food_rect);
    SDL_RenderPresent(renderer);
}

// Function to handle events
void handle_events(SDL_Event* event, Level* level) {
    if (event->type == SDL_QUIT) {
        SDL_Quit();
        exit(0);
    }
    if (event->type == SDL_KEYDOWN) {
        switch (event->key.keysym.sym) {
            case SDLK_UP:
                level->snake_direction = 0;
                break;
            case SDLK_DOWN:
                level->snake_direction = 1;
                break;
            case SDLK_LEFT:
                level->snake_direction = 2;
                break;
            case SDLK_RIGHT:
                level->snake_direction = 3;
                break;
        }
    }
}

int main() {
    // Initialize SDL
    initSDL();

    // Create window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    if (!window) {
        printf("Failed to create window: %s\n", SDL_GetError());
        exit(1);
    }

    // Create renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (!renderer) {
        printf("Failed to create renderer: %s\n", SDL_GetError());
        exit(1);
    }

    // Load images
    SDL_Texture* snake_texture = load_image("snake.png");
    SDL_Texture* food_texture = load_image("food.png");

    // Initialize game variables
    Level level = { 0, 0, 20, 20, 0, 0, 10, 10, 0 };

    // Main game loop
    SDL_Event event;
    int running = 1;
    while (running) {
        while (SDL_PollEvent(&event)) {
            handle_events(&event, &level);
        }

        // Update game state
        level.snake_x += level.snake_speed_x;
        level.snake_y += level.snake_speed_y;

        // Render game
        render_game(renderer, snake_texture, food_texture, &level);

        // Cap framerate
        SDL_Delay(1000 / FPS);
    }

    // Clean up
    SDL_DestroyTexture(snake_texture);
    SDL_DestroyTexture(food_texture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
```

