const { Client, GatewayIntentBits, PermissionsBitField, Events, CommandInteraction, SlashCommandBuilder } = require('discord.js');
const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMessages, GatewayIntentBits.MessageContent] });

// Define the ban command
const banCommand = new SlashCommandBuilder()
    .setName('ban')
    .setDescription('Bans a user from the server.')
    .addUserOption(option => option.setName('user').setDescription('The user to ban').setRequired(true))
    .addStringOption(option => option.setName('reason').setDescription('The reason for the ban').setRequired(false));

// Register the command
client.application?.commands.set([banCommand]);

// Event listener for interactionCreate
client.on(Events.InteractionCreate, async interaction => {
    if (!interaction.isChatInputCommand()) return;

    const command = interaction.commandName;
    if (command === 'ban') {
        const user = interaction.options.getUser('user');
        const reason = interaction.options.getString('reason') || 'No reason provided';

        // Check if the bot has permission to ban the user
        if (!interaction.member.permissions.has(PermissionsBitField.Flags.BanMembers)) {
            return interaction.reply({ content: 'You do not have permission to ban members.', ephemeral: true });
        }

        // Check if the user is a member of the server
        if (!interaction.guild.members.cache.has(user.id)) {
            return interaction.reply({ content: 'The user is not a member of this server.', ephemeral: true });
        }

        // Ban the user
        try {
            await interaction.guild.members.ban(user, { reason });
            return interaction.reply({ content: `Banned ${user.tag} for ${reason}.`, ephemeral: true });
        } catch (error) {
            return interaction.reply({ content: 'Failed to ban the user.', ephemeral: true });
        }
    }
});

// Login to the bot
client.login('YOUR_BOT_TOKEN');
```

Replace `'YOUR_BOT_TOKEN'` with your actual bot token. You can get your bot token from the Discord Developer Portal.

To run the bot, you can use the following command:

```sh
node index.js
```

