public class PowerOfThree {
    public static boolean isPowerOfThree(int n) {
        // Check if n is a power of 3
        if (n <= 0) {
            return false;
        }
        // If n is a power of 3, it will be a perfect cube
        return (Math.log10(n) / Math.log10(3)) % 1 == 0;
    }

