            std::transform(line.begin(), line.end(), line.begin(), ::tolower);

            // Count the occurrences of the word in the line
            size_t pos = 0;
            while ((pos = line.find(wordToCount, pos)) != std::string::npos) {
                count++;
                pos += wordToCount.length();
            }
        }

        // Close the file
        inputFile.close();

        // Print the count
        std::cout << "The word '" << wordToCount << "' appears " << count << " times in the file." << std::endl;
