#include <iostream>
#include <string>
#include <pugixml.hpp>

std::string convertMarkdownToHtml(const std::string& markdown) {
    pugi::xml_document doc;
    pugi::xml_parse_result result = doc.load_string(markdown.c_str());

    if (!result) {
        std::cerr << "Error parsing markdown: " << result.description() << std::endl;
        return "";
    }

    pugi::xml_node root = doc.first_child();
    if (root.type() != pugi::node_element) {
        std::cerr << "Root node is not an element" << std::endl;
        return "";
    }

    std::string html;
    pugi::xml_node html_node = doc.child("html");
    if (html_node.type() != pugi::node_element) {
        std::cerr << "HTML node is not an element" << std::endl;
        return "";
    }

    html_node.save(html);
    return html;
}

int main() {
    std::string markdown = "# Hello World\nThis is a markdown text.";
    std::string html = convertMarkdownToHtml(markdown);

    std::cout << "Markdown:\n" << markdown << "\n\nHTML:\n" << html << std::endl;

    return 0;
}
```

