function checkPrime(numbers) {
    // Ensure the input array has exactly 5 elements
    if (numbers.length !== 5) {
        console.log("Input array must contain exactly 5 numbers.");
        return;
    }

    // Loop through each number in the array
    for (let number of numbers) {
        // Check if the number is a prime number
        if (isPrime(number)) {
            console.log(number);
            break; // Exit the loop once a prime number is found
        }
    }
}

// Helper function to check if a number is a prime number
function isPrime(n) {
    if (n <= 1) return false;
    if (n <= 3) return true;

    // Check for even numbers and multiples of 3
    if (n % 2 === 0 || n % 3 === 0) return false;

    for (let i = 5; i * i <= n; i += 6) {
        if (n % i === 0 || n % (i + 2) === 0) return false;
    }

    return true;
}

// Example usage
const numbers = [2, 3, 4, 5, 6];
checkPrime(numbers);
```

