```cpp
#include <iostream>
#include <string>
#include <regex>
#include <unordered_map>

// Mapping of markdown syntax to HTML
std::unordered_map<std::string, std::string> markdownToHtml {
    {"# ", "<h1>"},
    {"## ", "<h2>"},
    {"### ", "<h3>"},
    {"#### ", "<h4>"},
    {"##### ", "<h5>"},
    {"###### ", "<h6>"},
    {"```\n.*\n```", "<pre><code>", true},
    {"`", "<code>", true},
    {"[ ](", "<a href=", true},
    {"http://", "<a href=http://", true},
    {"https://", "<a href=https://", true}
};

// Function to replace markdown syntax with HTML
std::string convertMarkdownToHtml(const std::string& markdown) {
    std::string html = markdown;
    for (const auto& [markdownPattern, htmlPattern, isSingleLine] : markdownToHtml) {
        std::regex regexPattern(markdownPattern, isSingleLine ? std::regex::single_line : std::regex::nosubs);
        html = std::regex_replace(html, regexPattern, htmlPattern);
    }
    return html;
}

