        if (texture == NULL) {
            printf("Unable to create texture from surface! SDL Error: %s\n", SDL_GetError());
        }
        SDL_FreeSurface(surface);
    }
    return texture;
}

int main(int argc, char* args[]) {
    // Initialize SDL
