        backgroundClip.x = 0;
        backgroundClip.y = 0;
        backgroundClip.w = SCREEN_WIDTH;
        backgroundClip.h = SCREEN_HEIGHT;
        SDL_RenderCopy(renderer, backgroundTexture, &backgroundClip, NULL);

